import React from 'react'
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import me from '../Assets/image.png';
import "../Style.css";

var Circle = React.createClass({
    render:function(){
      var circleStyle = {
        padding:10,
        margin:20,
        display:"inline-block",
        backgroundColor: this.props.bgColor,
        borderRadius: "50%",
        width:100,
        height:100,
      };
      return (
        <div style={circleStyle}>
        </div>
      );
    }
});

const About = () => {

  return (
    <Container className="my-5 mx-10">
      <Row>
        <Col xs={12} md={4}>
        <Circle bgColor={"#393E41"}/>
          <Image src={me} alt="Responsive image" roundedCircle fluid />
        </Col>
        <Col xs={12} md={8}  className="text-start">  
          <h2>About Me</h2> 
          <p>
            Hi ! I'm Jade. I'm a student at EPFL, currently finishing a master degree in neurotechnology.
            I'm highly interested in assistive technology. 
          </p>  
        </Col>
      </Row>
    </Container>
  );
}

export default About;
